var roxyFileman = globalUrl+'TinyMce/plugins/fileman/index.html?type=';
tinymce.PluginManager.add("dosya", function (t, e) {
    t.addButton("dosya", {
        text: "", icon: "browse", onclick: function () {
            t.windowManager.open({
                file: roxyFileman,
                title: 'Dosya Yoneticisi',
                width: 850,
                height: 650,
                resizable: "yes",
                inline: "yes",
                close_previous: "no"

            });
        }
    }),
    t.addMenuItem("dosya", {
        text: "Dosya Yoneticisi", context: "tools", onclick: function (e) {
            t.windowManager.open({
                file: roxyFileman,
                title: 'Dosya Yoneticisi',
                width: 850,
                height: 650,
                resizable: "yes",
                inline: "yes",
                close_previous: "no"

            });
        }
    })
});
